mapper.insert:
insert 
    into member
    (	mem_no
      , mem_id
      , mem_token
      , git_nick
      , avatar_url
      , mem_intro
      , create_date
      , status
    )
    values
    (
    	seq_mem.nextval
      , #{memId}
      , #{memToken}
      , #{gitNick}
      , #{avatarUrl}
      , #{memIntro}  
      , #{createDate}
      , #{status}
    )
