mapper.insert:

  insert into 
	         project 
	         (
	           pro_no
	         , pro_writer
	         , pro_stack
	         , pro_title
	         , pro_content
	         , pro_member
	         , pro_period
	         , pro_deadline
	         , pro_start_date
	         , pro_position
	         , pro_status
	         )
	         values
	         (
	           seq_pro.nextval
	         , #{proWriter}
	         , #{proStack}
	         , #{proTitle}
	         , #{proContent}
	         , #{proMember}
	         , #{proPeriod}
	         , #{proDeadLine}
	         , #{proStartDate}
	         , #{proPosition}
	         , 'Y'
	         )

mapper.update:
update project 
   set status = 'N'
 where pro_no = #{proNo}

update
       project
   set pro_stack = #{proStack}
     , pro_title = #{proTitle}
     , pro_content = #{proContent}
     , pro_member = #{proMember}
     , pro_period = #{proPeriod}
     , pro_deadLine = #{proDeadLine}
     , pro_start_date = #{proStartDate}
     , pro_position = #{proPosition}
 where pro_no = #{proNo}

mapper.select:
  		  select mem_no, pro_no
